DECLARE @intLetterID INT
DECLARE @recordCount INT

SELECT @recordCount = COUNT(*) FROM tblSMLetter  

IF EXISTS (SELECT * FROM tblSMLetter WHERE strName  = 'Recent Overdue Collection Letter')
	BEGIN
		UPDATE [dbo].[tblSMLetter] 
		SET [strName]				= 'Recent Overdue Collection Letter'
			, [strDescription]		= 'Recent Overdue Collection Letter'
			, [blbMessage]			= 
			, [ysnSystemDefined]	= 1	
			, [strModuleName]		= 'Sales'
			, [intConcurrencyId]	= 0			
		WHERE 
			strName  = 'Recent Overdue Collection Letter'				
	END
ELSE
	BEGIN
		IF @recordCount = 0 
			BEGIN
				SET @intLetterID = 1  
			END 
		ELSE
			BEGIN
				SELECT @intLetterID = MAX(intLetterId) + 1 FROM tblSMLetter 				
			END 
		
		INSERT [dbo].[tblSMLetter] 
		(
			[intLetterId]
			, [strName]
			, [strDescription]
			, [blbMessage]
			, [ysnSystemDefined]
			, [strModuleName]
			, [intConcurrencyId]
		) 
		VALUES 
		(
			@intLetterID
			, N'Recent Overdue Collection Letter'
			, N'Recent Overdue Collection Letter'
			, 
			, 1
			, N'Sales'
			, 0
		)					
	END

