SET IDENTITY_INSERT [dbo].[tblSMLetter] ON 

DECLARE @intLetterID INT
DECLARE @recordCount INT

SELECT @recordCount = COUNT(*) FROM tblSMLetter  

IF NOT EXISTS (SELECT * FROM tblSMLetter WHERE strName  = 'Recent Overdue Collection Letter')
BEGIN
	IF @recordCount = 0 
	BEGIN
		SET @intLetterID = 1  
	END 
	ELSE
	BEGIN
		SELECT @intLetterID = MAX(intLetterId) + 1 FROM tblSMLetter 
	END 
 
	IF NOT EXISTS (SELECT * FROM tblSMLetter WHERE intLetterId = @intLetterID)
	BEGIN		
		INSERT [dbo].[tblSMLetter] ([intLetterId], [strName], [strDescription], [blbMessage], [intConcurrencyId]) VALUES (@intLetterID, N'Recent Overdue Collection Letter', N'Recent Overdue Collection Letter', , 1)
	END
END

