
-- Patch script for 14.2 SP3
-- This will overwrite the existing Voucher Check Middle report with check layout that can be used for A/P. 
-- Note: The report layout will not work for Miscellaneous Checks. 

DECLARE @intReportId AS INT
DECLARE @intSubreportId AS INT

SELECT	TOP 1 
		@intReportId = intReportId 
FROM	tblRMReport 
WHERE	strName = 'Check Voucher Middle' AND strGroup = 'Cash Management'

SELECT	TOP 1 
		@intSubreportId = intReportId 
FROM	tblRMReport 
WHERE	strName = 'Voucher Check Middle Sub Report' AND strGroup = 'Sub Report'

-- Overwrite the Check Voucher Middle for A/P use. 
UPDATE tblRMReport 
SET blbLayout = 
WHERE intReportId = @intReportId

UPDATE tblRMDatasource
SET strQuery = 'EXEC uspCMVoucherCheckMiddleAPReport'
	,intDataSourceType = 1
WHERE intReportId = @intReportId

DELETE FROM tblRMSubreportSetting
WHERE intReportId = @intReportId

