	intReportId,
	intSubreportId,
	strControlName,
	intType,
	strParentField,
	strParentDataType,
	strChildField,
	strChildDataType,
	intConcurrencyId
)
SELECT 
	intReportId = @intReportId
	,intSubreportId = @intSubreportId
	,strControlName = 'VoucherCheckMiddleAP1'
