-- Setup for the report settings
-- Setup the first sub report (Check Voucher Middle Sub Report AP Payment)
BEGIN 
	SELECT	@intSubreportId = [intReportId]
	FROM	[dbo].[tblRMReport]
	WHERE	[strName] = N'Check Voucher Middle Sub Report AP Payment Overflow'

	IF (@intSubreportId IS NOT NULL)
	BEGIN 
		-- Setup the first sub report control
		INSERT [dbo].[tblRMSubreportSetting] (
			[intReportId], 
			[strControlName], 
			[intSubreportId], 
			[intConcurrencyId]
		)
		SELECT 
			[intReportId] = @intReportId,
			[strControlName] = N'VoucherCheckMiddleAP', 
			[intSubreportId] = @intSubreportId, 
			[intConcurrencyId] = 1

		SELECT @intSubreportSettingId = SCOPE_IDENTITY()

		IF (@intSubreportSettingId IS NOT NULL) 
		BEGIN 
			-- Setup the sub report filter 
			INSERT INTO [dbo].[tblRMSubreportFilter](
				[intSubreportSettingId]
				,[intType]
				,[strParentField]
				,[strParentDataType]
				,[strChildField]
				,[strChildDataType]
				,[intConcurrencyId]
			)
			SELECT 
				[intSubreportSettingId] = @intSubreportSettingId
				,[intType] = 0
				,[strParentField] = 'intTransactionId'
				,[strParentDataType] = 'Integer'
				,[strChildField] = 'intTransactionId'
				,[strChildDataType] = 'Integer'
				,[intConcurrencyId] = 1

			-- Setup the sub report condition 
			INSERT [dbo].[tblRMSubreportCondition] (
				[intSubreportSettingId], 
				[strBeginGroup], 
				[strEndGroup], 
				[strJoin], 
				[strFieldName], 
				[strFrom], 
				[strTo], 
				[strCondition], 
				[strDataType], 
				[intConcurrencyId]
			) 
			SELECT 
				[intSubreportSettingId] = @intSubreportSettingId, 
				[strBeginGroup] = N'', 
				[strEndGroup] = N'', 
				[strJoin] = N'And', 
				[strFieldName] = N'intBankTransactionTypeId', 
				[strFrom] = N'16', 
				[strTo] = N'', 
				[strCondition] = N'Equal To', 
				[strDataType] = N'Integer', 
				[intConcurrencyId] = 1
		END 

		
	END
END 

