				,[strParentDataType] = 'Integer'
				,[strChildField] = 'intTransactionId'
				,[strChildDataType] = 'Integer'
				,[intConcurrencyId] = 1

			-- Setup the sub report condition 
			INSERT [dbo].[tblRMSubreportCondition] (
				[intSubreportSettingId], 
				[strBeginGroup], 
				[strEndGroup], 
				[strJoin], 
				[strFieldName], 
				[strFrom], 
				[strTo], 
				[strCondition], 
				[strDataType], 
				[intConcurrencyId]
			) 
			SELECT 
				[intSubreportSettingId] = @intSubreportSettingId, 
				[strBeginGroup] = N'', 
				[strEndGroup] = N'', 
				[strJoin] = N'And', 
				[strFieldName] = N'intBankTransactionTypeId', 
				[strFrom] = N'3', 
				[strTo] = N'', 
				[strCondition] = N'Equal To', 
				[strDataType] = N'Integer', 
				[intConcurrencyId] = 1
		END 

	END
