CREATE PROCEDURE  [dbo].[uspGLImportOriginHistoricalJournal]
@intUserId		INT,
@result			NVARCHAR(500) = '' OUTPUT
AS

SET QUOTED_IDENTIFIER OFF SET ANSI_NULLS ON SET NOCOUNT ON SET XACT_ABORT ON
BEGIN TRANSACTION;
--===================================================================================================================================== -- DECLARE TEMPORARY TABLES --------------------------------------------------------------------------------------------------------------------------------------- CREATE TABLE #tmpPostJournals ( [intJournalId] [int] PRIMARY KEY, UNIQUE (intJournalId) );
CREATE TABLE #tmpValidJournals ( [intJournalId] [int] PRIMARY KEY, UNIQUE (intJournalId) );
DECLARE @Debit NUMERIC(18, 6) DECLARE @Credit NUMERIC(18, 6) DECLARE @DebitUnit NUMERIC(18, 6) DECLARE @CreditUnit NUMERIC(18, 6) DECLARE @GJDates DATETIME
--===================================================================================================================================== -- POPULATE JOURNALS TO POST TEMPORARY TABLE --------------------------------------------------------------------------------------------------------------------------------------- IF (ISNULL(@Param, '') <> '') INSERT INTO #tmpPostJournals EXEC (@Param) ELSE INSERT INTO #tmpPostJournals SELECT [intJournalId] FROM tblGLJournal
--===================================================================================================================================== -- UNPOSTING JOURNAL TRANSACTIONS --------------------------------------------------------------------------------------------------------------------------------------- IF ISNULL(@ysnPost, 0) = 0 BEGIN DECLARE @intCount AS INT
SET @Param = (SELECT strJournalId FROM tblGLJournal WHERE intJournalId IN (SELECT intJournalId FROM #tmpPostJournals)) EXEC [dbo].[uspGLReverseGLEntries] @strBatchId, @Param, @ysnRecap, 'AA', NULL, @intUserId, @intCount OUT SET @successfulCount = @intCount
IF(@intCount > 0) BEGIN UPDATE tblGLJournal SET ysnPosted = 0 WHERE intJournalId IN (SELECT intJournalId FROM #tmpPostJournals) END
GOTO Post_Commit; END
--===================================================================================================================================== -- JOURNAL VALIDATIONS --------------------------------------------------------------------------------------------------------------------------------------- IF ISNULL(@ysnRecap, 0) = 0 BEGIN -- DELETE Results 2 DAYS OLDER DELETE tblGLPostResult WHERE dtmDate < DATEADD(day, -1, GETDATE())
INSERT INTO tblGLPostResult (strBatchId,intTransactionId,strTransactionId,strDescription,dtmDate,intUserId,strTransactionType) SELECT @strBatchId as strBatchId,tmpBatchResults.intJournalId as intTransactionId,tblB.strJournalId as strTransactionId, strMessage as strDescription,GETDATE() as dtmDate,@intUserId,@strJournalType FROM ( --SELECT DISTINCT A.intJournalId, -- 'Unable to find an open fiscal year period to match the transaction date.' AS strMessage --FROM tblGLJournal A --WHERE A.intJournalId IN (SELECT intJournalId FROM #tmpPostJournals) AND ISNULL([dbo].isOpenAccountingDate(A.dtmDate), 0) = 0 --UNION --SELECT DISTINCT A.intJournalId, -- 'Unable to find an open fiscal year period to match the reverse date.' AS strMessage --FROM tblGLJournal A --WHERE 0 = CASE WHEN ISNULL(A.dtmReverseDate, '') = '' THEN 1 ELSE ISNULL([dbo].isOpenAccountingDate(A.dtmReverseDate), 0) END -- AND A.intJournalId IN (SELECT intJournalId FROM #tmpPostJournals) --UNION SELECT DISTINCT A.intJournalId, 'This transaction cannot be posted because the posting date is empty.' AS strMessage FROM tblGLJournal A WHERE 0 = CASE WHEN ISNULL(A.dtmDate, '') = '' THEN 0 ELSE 1 END AND A.intJournalId IN (SELECT intJournalId FROM #tmpPostJournals) UNION --SELECT DISTINCT A.intJournalId, -- 'Reverse date must be later than Post Date.' AS strMessage --FROM tblGLJournal A --WHERE 0 = CASE WHEN ISNULL(A.dtmReverseDate, '') = '' THEN 1 ELSE -- CASE WHEN A.dtmReverseDate <= A.dtmDate THEN 0 ELSE 1 END -- END AND A.intJournalId IN (SELECT intJournalId FROM #tmpPostJournals) --UNION SELECT DISTINCT A.intJournalId, 'You cannot post this transaction because it has inactive account id ' + B.strAccountId + '.' AS strMessage FROM tblGLJournalDetail A LEFT OUTER JOIN tblGLAccount B ON A.intAccountId = B.intAccountId WHERE ISNULL(B.ysnActive, 0) = 0 AND A.intJournalId IN (SELECT intJournalId FROM #tmpPostJournals) UNION SELECT DISTINCT A.intJournalId, 'You cannot post this transaction because it has invalid account(s).' AS strMessage FROM tblGLJournalDetail A LEFT OUTER JOIN tblGLAccount B ON A.intAccountId = B.intAccountId WHERE (A.intAccountId IS NULL OR 0 = CASE WHEN ISNULL(A.intAccountId, '') = '' THEN 0 ELSE 1 END) AND A.intJournalId IN (SELECT intJournalId FROM #tmpPostJournals) UNION SELECT DISTINCT A.intJournalId, 'You cannot post empty transaction.' AS strMessage FROM tblGLJournal A LEFT JOIN tblGLJournalDetail B ON A.intJournalId = B.intJournalId WHERE A.intJournalId IN (SELECT intJournalId FROM #tmpPostJournals) GROUP BY A.intJournalId HAVING COUNT(B.intJournalId) < 1 UNION SELECT DISTINCT A.intJournalId, 'Unable to post. The transaction is out of balance.' AS strMessage FROM tblGLJournalDetail A WHERE A.intJournalId IN (SELECT intJournalId FROM #tmpPostJournals) GROUP BY A.intJournalId HAVING SUM(ISNULL(A.dblCredit,0)) <> SUM(ISNULL(A.dblDebit,0)) UNION SELECT DISTINCT A.intJournalId, 'Retained Earnings is required.' AS strMessage FROM tblGLJournal A WHERE 0 = CASE WHEN ISNULL((SELECT TOP 1 1 FROM tblGLFiscalYear WHERE dtmDateFrom <= A.dtmDate and dtmDateTo >= A.dtmDate),1) = 0 THEN 0 ELSE CASE WHEN ISNULL((SELECT TOP 1 1 FROM tblGLFiscalYear WHERE dtmDateFrom <= A.dtmDate and dtmDateTo >= A.dtmDate and intRetainAccount IS NULL),0) = 1 THEN 0 ELSE 1 END END AND A.intJournalId IN (SELECT intJournalId FROM #tmpPostJournals) ) tmpBatchResults LEFT JOIN tblGLJournal tblB ON tmpBatchResults.intJournalId = tblB.intJournalId END
IF @@ERROR <> 0 GOTO Post_Rollback;
--===================================================================================================================================== -- POPULATE VALID JOURNALS TEMPORARY TABLE --------------------------------------------------------------------------------------------------------------------------------------- INSERT INTO #tmpValidJournals SELECT DISTINCT A.[intJournalId] FROM tblGLJournal A WHERE A.[intJournalId] IN (SELECT B.intJournalId FROM #tmpPostJournals B WHERE B.intJournalId NOT IN (SELECT intTransactionId FROM tblGLPostResult WHERE strBatchId = @strBatchId GROUP BY intTransactionId)) AND A.[ysnPosted] = 0 AND A.[strJournalType] = @strJournalType
IF @@ERROR <> 0 GOTO Post_Rollback;
IF NOT EXISTS(SELECT TOP 1 1 FROM #tmpValidJournals) BEGIN GOTO Post_Commit; END
--===================================================================================================================================== -- CHECK IF THE PROCESS IS RECAP OR NOT --------------------------------------------------------------------------------------------------------------------------------------- Post_Transaction:
DECLARE @intCurrencyId INT DECLARE @dblDailyRate NUMERIC (18,6)
IF ISNULL(@ysnRecap, 0) = 0 BEGIN
SET @intCurrencyId = (SELECT TOP 1 intCurrencyID FROM tblSMCurrency WHERE intCurrencyID = (CASE WHEN (SELECT TOP 1 strValue FROM tblSMPreferences WHERE strPreference = 'defaultCurrency') > 0 THEN (SELECT TOP 1 strValue FROM tblSMPreferences WHERE strPreference = 'defaultCurrency') ELSE (SELECT TOP 1 intCurrencyID FROM tblSMCurrency WHERE strCurrency = 'USD') END)) SET @dblDailyRate = (SELECT TOP 1 dblDailyRate FROM tblSMCurrency WHERE intCurrencyID = @intCurrencyId);
WITH Units AS ( SELECT A.[dblLbsPerUnit], B.[intAccountId] FROM tblGLAccountUnit A INNER JOIN tblGLAccount B ON A.[intAccountUnitId] = B.[intAccountUnitId] ) INSERT INTO tblGLDetail ( [strTransactionId] ,[intAccountId] ,[strDescription] ,[strReference] ,[dtmTransactionDate] ,[dblDebit] ,[dblCredit] ,[dblDebitUnit] ,[dblCreditUnit] ,[dtmDate] ,[ysnIsUnposted] ,[intConcurrencyId] ,[intCurrencyId] ,[dblExchangeRate] ,[intUserId] ,[dtmDateEntered] ,[strBatchId] ,[strCode] ,[strModuleName] ,[strTransactionForm] ,[strJournalLineDescription] ,[intJournalLineNo] ) SELECT [strTransactionId] = B.[strJournalId] ,[intAccountId] = A.[intAccountId] ,[strDescription] = A.[strDescription] ,[strReference] = 'AA Audit AdjustmentFY ' + CAST(YEAR(B.[dtmDate]) AS NVARCHAR(50)) ,[dtmTransactionDate] = A.[dtmDate] ,[dblDebit] = CASE WHEN [dblCredit] < 0 THEN ABS([dblCredit]) WHEN [dblDebit] < 0 THEN 0 ELSE [dblDebit] END ,[dblCredit] = CASE WHEN [dblDebit] < 0 THEN ABS([dblDebit]) WHEN [dblCredit] < 0 THEN 0 ELSE [dblCredit] END ,[dblDebitUnit] = ISNULL(A.[dblDebitUnit], 0) * ISNULL((SELECT [dblLbsPerUnit] FROM Units WHERE [intAccountId] = A.[intAccountId]), 0) ,[dblCreditUnit] = ISNULL(A.[dblCreditUnit], 0) * ISNULL((SELECT [dblLbsPerUnit] FROM Units WHERE [intAccountId] = A.[intAccountId]), 0) ,[dtmDate] = ISNULL(B.[dtmDate], GETDATE()) ,[ysnIsUnposted] = 0 ,[intConcurrencyId] = 1 ,[intCurrencyId] = @intCurrencyId ,[dblExchangeRate] = @dblDailyRate ,[intUserId] = @intUserId ,[dtmDateEntered] = GETDATE() ,[strBatchId] = @strBatchId ,[strCode] = 'AA' ,[strModuleName] = 'General Ledger' ,[strTransactionForm] = B.[strTransactionType] ,[strJournalLineDescription] = A.[strDescription] ,[intJournalLineNo] = A.intLineNo
FROM [dbo].tblGLJournalDetail A INNER JOIN [dbo].tblGLJournal B ON A.[intJournalId] = B.[intJournalId] WHERE B.[intJournalId] IN (SELECT [intJournalId] FROM #tmpValidJournals);
SET @GJDates = (SELECT dtmDate FROM tblGLJournal WHERE intJournalId = (SELECT TOP 1 [intJournalId] FROM #tmpValidJournals)) IF((SELECT ysnStatus FROM tblGLFiscalYear WHERE dtmDateFrom <= @GJDates AND dtmDateTo >= @GJDates) = 0) BEGIN
