--===================================================================================================================================== -- UPDATE GL SUMMARY RECORDS --------------------------------------------------------------------------------------------------------------------------------------- WITH JournalDetail AS ( SELECT [dtmDate] = ISNULL(A.[dtmDate], GETDATE()) ,[intAccountId] = A.[intAccountId] ,[dblDebit] = CASE WHEN [dblCredit] < 0 THEN ABS([dblCredit]) WHEN [dblDebit] < 0 THEN 0 ELSE [dblDebit] END ,[dblCredit] = CASE WHEN [dblDebit] < 0 THEN ABS([dblDebit]) WHEN [dblCredit] < 0 THEN 0 ELSE [dblCredit] END ,[dblDebitUnit] = [dblDebitUnit] ,[dblCreditUnit] = [dblDebitUnit] FROM [dbo].tblGLDetail A WHERE A.strBatchId = @strBatchId ) UPDATE tblGLSummary SET [dblDebit] = ISNULL(tblGLSummary.[dblDebit], 0) + ISNULL(GLDetailGrouped.[dblDebit], 0) ,[dblCredit] = ISNULL(tblGLSummary.[dblCredit], 0) + ISNULL(GLDetailGrouped.[dblCredit], 0) ,[dblDebitUnit] = ISNULL(tblGLSummary.[dblDebitUnit], 0) + ISNULL(GLDetailGrouped.[dblDebitUnit], 0) ,[dblCreditUnit] = ISNULL(tblGLSummary.[dblCreditUnit], 0) + ISNULL(GLDetailGrouped.[dblCreditUnit], 0) ,[intConcurrencyId] = ISNULL([intConcurrencyId], 0) + 1 FROM ( SELECT [dblDebit] = SUM(ISNULL(B.[dblDebit], 0)) ,[dblCredit] = SUM(ISNULL(B.[dblCredit], 0)) ,[dblDebitUnit] = SUM(ISNULL(B.[dblDebitUnit], 0)) ,[dblCreditUnit] = SUM(ISNULL(B.[dblCreditUnit], 0)) ,[intAccountId] = A.[intAccountId] ,[dtmDate] = ISNULL(CONVERT(DATE, A.[dtmDate]), '') FROM tblGLSummary A INNER JOIN JournalDetail B ON CONVERT(DATE, A.[dtmDate]) = CONVERT(DATE, B.[dtmDate]) AND A.[intAccountId] = B.[intAccountId] AND A.[strCode] = 'AA' GROUP BY ISNULL(CONVERT(DATE, A.[dtmDate]), ''), A.[intAccountId] ) AS GLDetailGrouped WHERE tblGLSummary.[intAccountId] = GLDetailGrouped.[intAccountId] AND tblGLSummary.[strCode] = 'AA' AND ISNULL(CONVERT(DATE, tblGLSummary.[dtmDate]), '') = ISNULL(CONVERT(DATE, GLDetailGrouped.[dtmDate]), '');
IF @@ERROR <> 0 GOTO Post_Rollback;
--===================================================================================================================================== -- INSERT TO GL SUMMARY RECORDS --------------------------------------------------------------------------------------------------------------------------------------- WITH Units AS ( SELECT A.[dblLbsPerUnit], B.[intAccountId] FROM tblGLAccountUnit A INNER JOIN tblGLAccount B ON A.[intAccountUnitId] = B.[intAccountUnitId] ), JournalDetail AS ( SELECT [dtmDate] = ISNULL(A.[dtmDate], GETDATE()) ,[intAccountId] = A.[intAccountId] ,[dblDebit] = CASE WHEN [dblCredit] < 0 THEN ABS([dblCredit]) WHEN [dblDebit] < 0 THEN 0 ELSE [dblDebit] END ,[dblCredit] = CASE WHEN [dblDebit] < 0 THEN ABS([dblDebit]) WHEN [dblCredit] < 0 THEN 0 ELSE [dblCredit] END ,[dblDebitUnit] = ISNULL(A.[dblDebitUnit], 0) * ISNULL((SELECT [dblLbsPerUnit] FROM Units WHERE [intAccountId] = A.[intAccountId]), 0) ,[dblCreditUnit] = ISNULL(A.[dblCreditUnit], 0) * ISNULL((SELECT [dblLbsPerUnit] FROM Units WHERE [intAccountId] = A.[intAccountId]), 0) FROM [dbo].tblGLDetail A WHERE A.strBatchId = @strBatchId ) INSERT INTO tblGLSummary ( [intAccountId] ,[dtmDate] ,[dblDebit] ,[dblCredit] ,[dblDebitUnit] ,[dblCreditUnit] ,[strCode] ,[intConcurrencyId] ) SELECT [intAccountId] = A.[intAccountId] ,[dtmDate] = ISNULL(CONVERT(DATE, A.[dtmDate]), '') ,[dblDebit] = SUM(A.[dblDebit]) ,[dblCredit] = SUM(A.[dblCredit]) ,[dblDebitUnit] = SUM(A.[dblDebitUnit]) ,[dblCreditUnit] = SUM(A.[dblCreditUnit]) ,[strCode] = 'AA' ,[intConcurrencyId] = 1 FROM JournalDetail A WHERE NOT EXISTS ( SELECT TOP 1 1 FROM tblGLSummary B WHERE ISNULL(CONVERT(DATE, A.[dtmDate]), '') = ISNULL(CONVERT(DATE, B.[dtmDate]), '') AND A.[intAccountId] = B.[intAccountId] AND B.[strCode] = 'AA' ) GROUP BY ISNULL(CONVERT(DATE, A.[dtmDate]), ''), A.[intAccountId];
IF @@ERROR <> 0 GOTO Post_Rollback;
--===================================================================================================================================== -- UPDATE JOURNAL TABLE --------------------------------------------------------------------------------------------------------------------------------------- UPDATE tblGLJournal SET [ysnPosted] = 1 ,[dtmPosted] = GETDATE() WHERE [intJournalId] IN (SELECT [intJournalId] FROM #tmpValidJournals);
IF @@ERROR <> 0 GOTO Post_Rollback;
--===================================================================================================================================== -- UPDATE RESULT --------------------------------------------------------------------------------------------------------------------------------------- INSERT INTO tblGLPostResult (strBatchId,intTransactionId,strTransactionId,strDescription,dtmDate,intUserId,strTransactionType) SELECT @strBatchId as strBatchId,intJournalId as intTransactionId,strJournalId as strTransactionId, strMessage as strDescription,GETDATE() as dtmDate,@intUserId,@strJournalType FROM ( SELECT DISTINCT A.intJournalId,A.strJournalId, 'Transaction successfully posted.' AS strMessage FROM tblGLJournal A WHERE A.intJournalId IN (SELECT intJournalId FROM #tmpValidJournals) ) B
IF @@ERROR <> 0 GOTO Post_Rollback;
--===================================================================================================================================== -- RETURN TOTAL NUMBER OF VALID JOURNALS --------------------------------------------------------------------------------------------------------------------------------------- SET @successfulCount = ISNULL(@successfulCount,0) + (SELECT COUNT(*) FROM #tmpValidJournals)
--===================================================================================================================================== -- FINALIZING STAGE --------------------------------------------------------------------------------------------------------------------------------------- Post_Commit: COMMIT TRANSACTION GOTO Post_Exit
Post_Rollback: ROLLBACK TRANSACTION GOTO Post_Exit
Post_Exit: IF EXISTS (SELECT 1 FROM tempdb..sysobjects WHERE id = object_id('tempdb..#tmpPostJournals')) DROP TABLE #tmpPostJournals IF EXISTS (SELECT 1 FROM tempdb..sysobjects WHERE id = object_id('tempdb..#tmpValidJournals')) DROP TABLE #tmpValidJournals

