CREATE TABLE #tmpValidJournals ( [intJournalId] [int] PRIMARY KEY, UNIQUE (intJournalId) );
CREATE TABLE #tmpReverseJournals ( [intJournalId] [int] PRIMARY KEY, UNIQUE (intJournalId) );
