CREATE PROCEDURE [dbo].[uspCreateCertificate]
	@directory nvarchar(max) = null
WITH ENCRYPTION
AS
BEGIN
  IF EXISTS (SELECT TOP 1 1 FROM sys.certificates WHERE name = 'iRelyi21Certificate')
  BEGIN
    DROP CERTIFICATE iRelyi21Certificate 
  END
  DECLARE @version int = SUBSTRING(CAST(SERVERPROPERTY('productversion') AS varchar), 1, CHARINDEX('.', CAST(SERVERPROPERTY('productversion') AS varchar)) - 1)
  IF @version = 10 AND @directory IS NOT NULL
  BEGIN
  EXEC('
    CREATE CERTIFICATE iRelyi21Certificate
    FROM FILE = ''' + @directory + '\iRelyi21Certificate.cer''
    WITH PRIVATE KEY (
      FILE = ''' + @directory + '\iRelyi21Certificate.pvk'',
      DECRYPTION BY PASSWORD = ''5b027c081e37b'',
      ENCRYPTION BY PASSWORD = ''neYwLw+SCUq84dAAd9xuM1AFotK5QzL4Vx4VjYUemUY=''
    );
  ')
  END
  IF @version > 10
  BEGIN
  EXEC('
    CREATE CERTIFICATE iRelyi21Certificate
    FROM BINARY = 
    WITH PRIVATE KEY (
      BINARY = ,
      DECRYPTION BY PASSWORD = ''5b027c081e37b'',
      ENCRYPTION BY PASSWORD = ''neYwLw+SCUq84dAAd9xuM1AFotK5QzL4Vx4VjYUemUY=''
    );
  ')
  END
END